import React, {Component} from "react";
import Navber from '../Navber/Navber';
import Footer from '../Footer';
import axios from 'axios';
class Appointment extends Component{
    constructor() {
        super()
        this.state = {
          first_name: '',
          last_name: '',
          email: '',
          address: '',
          phone_no: '',
          dateTime: '',
          errors: {}
        }
    
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
      }
    
      onChange(e) {
        this.setState({ [e.target.name]: e.target.value })
      }
      onSubmit(e) {
        e.preventDefault()
    
        const newUser = {
          first_name: this.state.first_name,
          last_name: this.state.last_name,
          email: this.state.email,
          address: this.state.address,
          phone_no: this.state.phone_no,
          dateTime: this.state.dateTime
        }
        axios.post('/appointment/register', newUser)
          .then(responce => {
            console.log('Registered');
            return responce.data;
          }) 
          .then(res => {
            if(res === 'user already exist...') {
              this.setState({errors: res})
            }else {
              this.props.history.push(`/appointment/login`)
            }
          })
      }    
    render(){
        return(
        <div className="body">
            <Navber />
          <div className="container my-5">
            <div className="row">
              <div className="col-md-6 mt-5 mx-auto">
                <form noValidate onSubmit={this.onSubmit} >
                  <h1 className="h3 mb-3 font-weight-normal btn-rg">Appointment Booking</h1>
                  <div className="form-group">
                    <label htmlFor="name">First name</label>
                    <input
                      type="text"
                      className="form-control"
                      name="first_name"
                      placeholder="Enter your first name"
                      value={this.state.first_name}
                      onChange={this.onChange}
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="name">Last name</label>
                    <input
                      type="text"
                      className="form-control"
                      name="last_name"
                      placeholder="Enter your last name"
                      value={this.state.last_name}
                      onChange={this.onChange}
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="email">Email address</label>
                    <input
                      type="email"
                      className="form-control"
                      name="email"
                      placeholder="Enter email"
                      value={this.state.email}
                      onChange={this.onChange}
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="name">Address</label>
                    <input
                      type="text"
                      className="form-control"
                      name="address"
                      placeholder="Enter your Resident Address"
                      value={this.state.address}
                      onChange={this.onChange}
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="name">Phone Number</label>
                    <input
                      type="text"
                      className="form-control"
                      name="phone_no"
                      placeholder="Enter your Phone Number"
                      value={this.state.phone_no}
                      onChange={this.onChange}
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="name">Date and Time</label>
                    <input
                      type="datetime-local"
                      step="600000"
                      className="form-control"
                      name="dateTime"
                      placeholder="Enter your Phone Number"
                      value={this.state.dateTime}
                      onChange={this.onChange}
                    />
                  </div>
                  <button
                    type="submit"
                    className="btn btn-lg btn-primary btn-block"
                  >
                    Register!
                  </button>
                </form>
              </div>
            </div>
          </div>
          
          <Footer />
          </div>
        )
    }
}
export default Appointment;
